mapper.insert:
insert into alarm_chat(alarm_member_no, alarm_division, alarm_contents_pk, chatroom_no)

values
	(
	   #{member_no},
	   #{alarm.alarm_division},
	   #{alarm.alarm_contents_pk},
	   #{alarm.chatroom_no}
	);

mapper.select:
select *, count(*) as alarm_group_count
from( select m.alarm_no, m.alarm_member_no, m.alarm_division, m.alarm_contents_pk, n.chatlog_no, n.member_no, n.chatroom_no, n.chatlog_log, n.chatlog_division, n.chatlog_date, p.member_id
from alarm_chat as m
left join chatlog as n on m.alarm_contents_pk = n.chatlog_no
left join member as p on n.member_no = p.member_no
where m.alarm_member_no=#{member_no}
order by m.alarm_no desc
limit 18446744073709551615
) as A
group by chatroom_no;

select count(alarm_member_no) as alarm_count

from alarm_chat

where alarm_member_no=#{member_no}
